import java.util.concurrent.locks.ReentrantLock;

class Inventory {
    int items = 6;
    private final ReentrantLock lock = new ReentrantLock();
    public void sell(String cust, int cnt) {
        lock.lock();
        try {
            if (items >= cnt) {
                items -= cnt;
                System.out.println(cust + " purchased " + cnt + " items.");
            } else {
                System.out.println(cust + " failed to purchase " + cnt + " items.");
            }
        } finally {
            lock.unlock();
        }
    }
}

class Seller implements Runnable {
    private Inventory inventory;
    private String customer;
    private int quantity;
    Seller(Inventory in, String cust, int qty) {
        inventory = in;
        customer = cust;
        quantity = qty;
    }
    public void run() {
        inventory.sell(customer, quantity);
    }
}

public class Store {
