public class ActivationRecordExample {
    public static void main(String[] args) {
        int result = factorial(5);
        System.out.println("Factorial of 5 is: "+result);
    }
    public static int factorial (int n) {
        if (n == 1) {
            return 1;
        } else {
            return n * factorial(n - 1);
        }
    }
}

public class CounterExample {
    public static void main(String[] args) {
        int counter = 5;
        do {
            counter--;
        } while (counter > 0);
        switch(counter) {
            case 0: System.out.println("counter zero");
                    break;
            case 1: System.out.println("counter one");
            case 2: System.out.println("counter two");
            case 3: System.out.println("counter three");
            case 4: System.out.println("counter four");
            case 5: System.out.println("counter five");
                    break;
            default: System.out.println("No counter available");
        }
    }
}

