import sys
from flask import Flask
app = Flask(__name__)
def do_something():
    if len(sys.argv) >= 2:
        l = []
        for i in range(1, len(sys.argv)):
            l.append(int(sys.argv[i]))
        return l
    else:
        return None
@app.route("/")
def do_sum():
    result = do_something()
    if result:
        s = 0
        for e in result:
            s += int(e)
        return f"<h2>Sum is: {s}</h2>"
    else:
        return f"<h2>Sum is: {result}</h2>"
app.run(debug=True)
